mapper.select:
select status, count(status)
    from

        group by status;

mapper.update:
UPDATE cases
SET status=#{status, jdbcType=VARCHAR}, updatedDate=#{updatedDate, jdbcType=DATE}
where cid=#{cid, jdbcType=VARCHAR};

mapper.delete:
DELETE FROM
           cases
       WHERE cid=#{cid, jdbcType=VARCHAR};

mapper.update:
UPDATE cases
SET email=#{email, jdbcType=VARCHAR}
where cid=#{cid, jdbcType=VARCHAR};

UPDATE cases
set done=true
where cid=#{cid, jdbcType=VARCHAR};
